import styles from "./Footer.module.css";
import FullWidthSection from "./FullWidthSection";
import CentralSection from "./CentralSection";

const Footer = (props) => {
  return (
    <FullWidthSection
      style={{
        backgroundColor: "rgb(81,81,81)",
        height: "15rem",
      }}>
      <CentralSection>
        <div className={styles.container}>
          <FooterTile
            header={"Contact"}
            text={"(604) 723-5351 \n info@heliosolar.com"}
          />
          <div className={styles.item}>
            <div className={styles.itemHeader}>
              <b>About</b>
            </div>
            <div className={styles.itemText} style={{ maxWidth: "32rem" }}>
              Helio solar is a new solar intallation company started by the
              owners after working for a number of years at one of the biggest
              solar companies in Canada. All of our staff are certified NABCEP
              solar PV associates, and we staff a registered electrician on
              every job.
            </div>
          </div>
        </div>
      </CentralSection>
    </FullWidthSection>
  );
};

const FooterTile = (props) => {
  return (
    <div className={styles.item}>
      <div className={styles.itemHeader}>{props.header}</div>
      <div className={styles.itemText}>{props.text}</div>
    </div>
  );
};

export default Footer;
