// Function to use stately variables to update things that have changes based onsmall medium and alrge window sizes
export const toggleSML = (scheduler, width, sml, med, lrg, fn) => {
  width < process.env.REACT_APP_BREAKPOINT_S
    ? fn(sml)
    : width > process.env.REACT_APP_BREAKPOINT_L
    ? fn(lrg)
    : fn(med);
};

export const executeOnResize = (functions) => {
  const doWork = (fns) => {
    for (let fn of fns) {
      fn.function(fn.args);
    }
  };
  window.addEventListener("resize", () => doWork(functions));
};
