import styles from "./Modal.module.css";
import { selectTSML } from "../../utils";
import closeButton from "../../images/close_button.png";
import { useState } from "react";
import { handleToggleModal } from "../../utils";

const Modal = (props) => {
  const w = props.viewportWidth;
  const modalZoom = selectTSML(w, "0.9", "0.8", "1", "1");
  const [modalVis, setModalVis] = useState();

  const onToggleModal = () => {
    handleToggleModal(setModalVis);
  };

  return (
    <div
      className={`${styles.container} noscroll`}
      style={{
        opacity: modalVis.opacity,
        visibility: modalVis.visibility,
      }}>
      <div className={styles.blackout}></div>
      <div
        className={`${styles.modal} noscroll`}
        style={{
          width: props.modalWidth,
          zoom: modalZoom,
          height: props.modalHeight,
        }}>
        <div className={styles.sidePanel}>{props.sidePanel}</div>
        <div className={styles.mainPanel}>
          <div className={styles.headerContainer}>
            <div className={styles.headerSpacer}></div>
            <img
              src={closeButton}
              className={styles.closeModalButton}
              alt="exit modal button"
              onClick={onToggleModal}
            />
          </div>

          {props.children}
        </div>
      </div>
    </div>
  );
};

export default Modal;
