import styles from "./App.module.css";
import NavBar from "./components/Nav/NavBar";
import CentralSection from "./components/CentralSection";
import CentralTile from "./components/CentralTile";
import ProductCarousel from "./components/ProductCarousel";
import QnaCard from "./components/QnaCard";
import Footer from "./components/Footer";
import Banner1 from "./components/Homepage/Banner1";
import SolarPrograms from "./components/Homepage/SolarPrograms";
import SolarSolutions from "./components/Homepage/SolarSolutions";

import React, { useState } from "react";
import { useWindowSize } from "usehooks-ts";

function App() {
  const wSmall = 1100;
  const wLarge = 1600;
  const { width } = useWindowSize();

  // Stately styling variables
  const [QnaTemplateColumns, setQnaTemplateColumns] = useState("50% 50%");
  const [footerFlexDirection, setFooterFlexDirection] = useState("row");
  const [footerTextMaxWidth, setFooterTextMaxWidth] = useState("32rem");

  // Function to use stately variables to update things that have changes based onsmall medium and alrge window sizes
  const toggleSmallMedLarge = (sml, med, lrg, fn) => {
    width < wSmall ? fn(sml) : width > wLarge ? fn(lrg) : fn(med);
  };

  // Handles resize for the app
  let resizeUpdateScheduled = false;
  const handleResize = () => {
    if (!resizeUpdateScheduled) {
      resizeUpdateScheduled = true;
      setTimeout(() => {
        toggleSmallMedLarge("grid", "flex", "flex", setPTDisplay);
        toggleSmallMedLarge("center", "", "", setPTJustify);
        toggleSmallMedLarge("80%", "20%", "20%", setPTWidth);
        toggleSmallMedLarge(
          "100%",
          "50% 50%",
          "50% 50%",
          setQnaTemplateColumns
        );
        toggleSmallMedLarge("column", "row", "row", setFooterFlexDirection);
        toggleSmallMedLarge("", "32rem", "32rem", setFooterTextMaxWidth);
        resizeUpdateScheduled = false;
      }, 50);
    }
  };

  window.addEventListener("resize", handleResize);

  // App onload function
  const onLoad = () => {
    handleResize();
  };

  return (
    <div className={styles.App} onLoad={onLoad}>
      <NavBar />
      <Banner1 viewportWidth={width} />
      <SolarPrograms viewportWidth={width} />
      <SolarSolutions viewportWidth={width} />
      <CentralSection
        style={{ flexDirection: "column", borderBottom: "1px solid grey" }}>
        <div
          style={{
            width: "100%",
            textAlign: "center",
            fontSize: "2.5rem",
            marginTop: "3rem",
          }}>
          Our <b>Products</b>
        </div>
        <div style={{ margin: "4rem 0rem 5rem 0rem" }}>
          <ProductCarousel wSmall={wSmall} />
        </div>
      </CentralSection>
      <CentralSection style={{ flexDirection: "column" }}>
        <div
          style={{
            width: "100%",
            fontSize: "2rem",
            textAlign: "center",
          }}>
          FAQ
        </div>
        <div
          style={{
            display: "grid",
            gridTemplateColumns: QnaTemplateColumns,
            justifyContent: "center",
            width: "100%",
            overflow: "scroll",
            msOverflowStyle: "none" /* for Internet Explorer, Edge */,
            scrollbarWidth: "none" /* for Firefox */,
            overflowY: "scroll",
            borderBottom: "1px solid grey",
            paddingBottom: "4rem",
          }}>
          {[
            {
              q: "Is it sunny enough in Vancouver and BC generally for solar to be worth it?",
              a: "Yes! It is a common misconception that British Columbia is too rainy or overcast for solar, specificallt Vancouver and Victoria. In fact, BC receives the same amount of energy from the sun as Germany, a global leader in solar energy.",
            },
            {
              q: "How much does a typical system cost, and how long will it take to pay itself off?",
              a: "In BC, the average cost of a solar ystem to offset the yearly energy usage of a home is $20,000 to $30,000 (not including battery backup). The payback period is very dependent on site conditions, shading, roof angle, and region, but is typially between 10-15 years.",
            },
            {
              q: "What are the product warranties and who will be around in 10 years to replace equipment if it malfuncitons?",
              a: "We choose inverters and solar panels that come with a 25-year manufacturer warranty, and the brands we use are among the largest solar companies globally, giving you the most assurance that they will exists down the road if equipment fails. Additionally, all of our work is backed by a 5-year workmanship warranty.",
            },
            {
              q: "How long will it take to install solar on my home?",
              a: "Despite the actual installation of solar panels taking only 2-4 days typically, solar project have a timeline of 3-12 weeks (depending on regional permitting requirements). The majority of delays come from waiting for a building permit if one is required. This can take anywhere from a week to 8 weeks depending on the municipality. Will will make sure we give you a realistic timeline based on your region's permit requirements.",
            },
          ].map((item) => (
            <CentralTile>
              <QnaCard question={item.q} answer={item.a} />
            </CentralTile>
          ))}
        </div>
      </CentralSection>
      <CentralSection>
        <div
          style={{
            width: "100%",
            display: "flex",
            flexDirection: "row",
            justifyContent: "center",
            margin: "2rem 0 5rem 0",
          }}>
          <div
            className={`${styles.myButton} ${styles.myButtonDark}`}
            style={{
              backgroundColor: "rgb(64,136,202)",
              color: "white",
              fontSize: "1.8rem",
              width: "100%",
              height: "3rem",
            }}>
            Request Free Solar Quote!
          </div>
        </div>
      </CentralSection>
      <Footer
        footerFlexDirection={footerFlexDirection}
        footerTextMaxWidth={footerTextMaxWidth}
      />
    </div>
  );
}

export default App;
