import FullWidthSection from "../FullWidthSection";
import BlackFade from "../BlackFade";
import { useState } from "react";
import { toggleSML } from "../../utils";
import styles from "./Banner1.module.css";

import background_m from "../../images/solarspan3.jpg";
import background_s from "../../images/solarspan3_small.jpg";
import background_l from "../../images/solarspan3_large.jpg";
import nabcepSeal from "../../images/pv_assoc_seal.png";

const Banner1 = (props) => {
  const [background, setBackground] = useState(background_m);

  toggleSML(
    props.width,
    background_s,
    background_m,
    background_l,
    setBackground
  );

  return (
    <FullWidthSection style={{ marginTop: "var(--navBarHeight)" }}>
      <BlackFade backgroundImage={background} backgroundOpacity="0.7">
        <div className={styles.bannerContainer}>
          <div className={styles.bannerText}>
            Residential solar installation<br></br>
            Pre-wiring and consultation<br></br>
            Off-grid and grid-tied projects<br></br>
          </div>
          <img
            className={styles.bannerNabcep}
            src={nabcepSeal}
            alt="nabcep seal"
          />
        </div>
      </BlackFade>
    </FullWidthSection>
  );
};

export default Banner1;
