import styles from "./Modal.module.css";
import { selectSML } from "../../utils";
import { Component } from "react";

class Modal extends Component {
  handleScroll = (e) => {
    console.log("scrolling!");
  };

  componentDidMount() {
    console.log("modalmounted");
    window.removeEventListener("scroll", this.handleScroll, true);
  }

  componentWillUnmount() {
    window.removeEventListener("scroll", this.handleScroll);
  }

  render() {
    const w = this.props.viewportWidth;
    const modalWidth = selectSML(w, "90%", "800px", "800px");
    const modalHeight = selectSML(w, "90%", "", "");

    return (
      <div
        className={styles.container}
        style={{
          opacity: this.props.modalVis.opacity,
          visibility: this.props.modalVis.visibility,
        }}
        onScroll={this.props.onScroll}>
        <div className={styles.blackout}></div>
        <div
          className={styles.modal}
          style={{ width: modalWidth, height: modalHeight }}>
          <div className={styles.modalTitle}>{this.props.title}</div>
          <div className={styles.closeModalButton}></div>
          <div>{this.props.children}</div>
        </div>
      </div>
    );
  }
}

export default Modal;
